.reviews-box {
position: relative;
width: 100%;
height: 300px;
overflow: hidden;
  }

.reviews-slider {
justify-content: center; /* Adicionado para centralizar os slides horizontalmente */
display: flex;
width: 350%;
height: 100%;
overflow: hidden;
transition: transform 0.5s ease-in-out; /* Alteração do tempo de transição */
  }

.slide {
flex: 1;
display: flex;
align-items: center;
justify-content: center;
flex-direction: column;
text-align: center;
 margin-right: 10; 
  }

.slide img {
width: 100%;
height: 100%;
  }

.slider-controls {
position: absolute;
top: 50%;
transform: translateY(-50%);
display: flex;
justify-content: space-between;
width: 100%;
height: 30px;
z-index: 1;
  }

.slider-controls button {
background-color:transparent;
color:#000000;
border: none;
font-size: 30px;
cursor: pointer;
  }

h2 {
font-size: 30px;
color: #000000;
font-weight: bold;
text-align: center;
margin-top: 0;
margin-bottom: 30px;
}
