.container {
max-width: 100%;
display: flex;
flex-direction: column;
align-items: center;
}

.form {
width: 100%;
max-width: 600px;
display: flex;
flex-direction: column;
align-items: center;
}

.form label {
display: block;
margin-bottom: 10px;
text-align: center;
}

.form input[type="text"],
.form input[type="email"],
.form button {
width: 100%;
padding: 10px;
margin-bottom: 10px;
box-sizing: border-box;
font-size: 16px;
}

.form button {
margin-top: 10px;
}

@media only screen and (min-width: 768px) {
.form {
flex-direction: row;
justify-content: center;
}

.form label {
margin-right: 10px;
margin-bottom: 10;
text-align: left;
}

.form input[type="text"],
.form input[type="email"] {
width: 250px;
margin-right: 10px;
margin-bottom: 0;
}

.form button {
width: auto;
margin-top: 0;
}

.form label {
color: #000000;
}

.newsletter-box {
background-color: #CCCCCC;
padding: 20px;
border: 1px solid #ddd;
border-radius: 5px;
text-align: center;
margin: 10px;
}

.newsletter-box form {
justify-content: center;
align-items: center;
}

.newsletter-box .form-group {
  width: 40%;
  margin-right: 10px;
}

.newsletter-box h3 {
color: #000000;
margin-top: 0;
}

.newsletter-box button {
background-color: #000000;
color: #FFFFFF;
padding: 10px;
margin-bottom: 10;
border: none;
border-radius: 3px;
cursor: pointer;
transition: background-color 0.3s ease;
flex-grow: 1;
}

.newsletter-box button:hover {
background-color: #08e1f4;
}

.sp-checkbox-option label {
color: #000000;
}

.sp-checkbox-option a {
color: #000000;
}

.sp-checkbox-option input[type="checkbox"] + span {
color: #000000;
}
